pub const INDEX_HTML: &str = r#"<!DOCTYPE html><html><head><title>gnostr</title><script src="https://unpkg.com/htmx.org@1.9.10"></script><style>body{background:#000;color:#0f0;font-family:monospace;padding:20px}.post{border:1px solid #0f0;padding:10px;margin:10px 0}</style></head><body><div style="max-width:800px;margin:0 auto"><h1>gnostr://node</h1><p>ID: {{peer_id}}</p><form hx-post="/publish" hx-swap="none" hx-on::after-request="this.reset()"><input name="author" placeholder="Author"><input name="title" placeholder="Title"><textarea name="content"></textarea><button type="submit" style="width:100%;background:#0f0">BROADCAST</button></form><div hx-get="/feed" hx-trigger="load, every 5s, newPost from:body"></div><h2>Repos</h2>{% for r in repos %}<div class="post"><strong>{{r.name}}</strong><br><code>git clone gnostr://localhost:3000/git/{{r.path}}</code></div>{% endfor %}</div></body></html>"#;
pub const FEED_HTML: &str = r#"{% for p in posts %}<div class="post"><strong>{{p.title}}</strong> by {{p.author}}<p>{{p.content}}</p></div>{% endfor %}"#;
